import axios from 'axios';
import React, { useCallback, useEffect, useState, Component } from 'react';
import { StyleSheet, Text, View, SafeAreaView } from 'react-native';
import * as SplashScreen from 'expo-splash-screen';
import * as Font from 'expo-font';
import AppLoading from 'expo-app-loading';
import Header from '../components/Header';
import List from '../components/List';
import * as Location from 'expo-location';

const PrimeraPantalla = ({navigation}) => 
{
  const [location, setLocation] = useState(null);
  const [errorMsg, setErrorMsg] = useState(null);
  const [loading, setLoading] = useState(true);

  const [firstDeas, setFirstDea] = useState([]);

  const config = {
    headers:{
      UserLocation: location,
    }
  };

  const getDEA = () => 
  {
    axios.get('/dea', config)
      .then(res => 
      {
        setLoading(false);
        setFirstDea(res.data);
      }).catch(error => console.log(error));
  }

  useEffect(() => {
    setLoading(true);
    (async () => {
      let { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        setErrorMsg('Permission to access location was denied');
        console.log(errorMsg);
        return;
      }

      let location = await Location.getCurrentPositionAsync({});
      setLocation(location);
    })();

    getDEA();
  }, []);

  return (
    loading ? <Image src='../assets/splash.png'/> : (
    <SafeAreaView style={styles.container}>
      <Header navigation={navigation}></Header>
      <Text style={{margin: '3%', fontWeight: 'bold', color: '#FFF', fontSize: 17}}>M√ÅS CERCANOS:</Text>
      <List deas={firstDeas}></List>
    </SafeAreaView>
    )
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FF0000',
    alignItems: 'center',
    justifyContent: 'center',
    //fontFamily: 'Poppins'
  }
});

export default PrimeraPantalla;
