import axios from 'axios';
import React, { useCallback, useEffect, useState, Component } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import * as SplashScreen from 'expo-splash-screen';
import * as Font from 'expo-font';
import AppLoading from 'expo-app-loading';
import Header from './components/Header';
import List from './components/List';

axios.defaults.baseURL = 'http://localhost:3000';
axios.defaults.headers.common['Authorization'] = 'AUTH TOKEN';
axios.defaults.headers.post['Content-Type'] = 'application/json';

export default function App() {
  const [firstDeas, setFirstDea] = useState([]);

  const getDEA = () => axios.get('/dea').then(res => setFirstDea(res.data)).catch(error => console.log(error));

  useEffect(() => {getDEA();}, []);

  return (
    <View style={styles.container}>
      <Header></Header>
      <View style={styles.masCercanos}><Text style={{fontWeight: 'bold', color: '#FFF', fontSize: 17}}>M√ÅS CERCANOS:</Text></View>
      <List deas={firstDeas}></List>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FF0000',
    alignItems: 'center',
    justifyContent: 'center',
    //fontFamily: 'Poppins'
  },
  masCercanos: {
    position: 'absolute',
    width: 168,
    height: 30,
    alignItems: 'center',
    top: 150
  }
});
